import React, { useState } from 'react';
import './login.css';

function LoginForm({ onLogin = () => {} }) {
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState(''); // optional if you need a username
  const [password, setPassword] = useState('');
  const [password2, setPassword2] = useState('');


  

  const handleSubmit = (event) => {
    event.preventDefault();

     if (!email || !password) {
      alert('Please fill out all required fields.');
      return;
    }
    if(password != password2) {
        alert("Passwords do not match.");
        return; 
    }
    if(username == '') {
        username = "Anonymous"
    }

    // Call the onLogin callback with the form data
    onLogin({ email, username, password });
    console.log('Form Data:', { email, username, password });
  };

  return (
    <form onSubmit={handleSubmit} className="login-form">
      <label className="Header2" htmlFor="email-input">
        Email:
      </label>
      <input
        className="form-input"
        id="email-input"
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
      />

      <br />

      {/* Remove or rename this field if your app doesn't need a username */}
      <label className="Header2" htmlFor="username-input">
        Username (optional):
      </label>
      <input
        className="form-input"
        id="username-input"
        type="text"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />

      <br />

      <label className="Header2" htmlFor="password-input">
        Password:
      </label>
      <input
        className="form-input"
        id="password-input"
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
      />
    <label className="Header2" htmlFor="password-input">
         Re-Enter Password:
    </label>
      <input 
        className="form-input"
        id = "password2-input"
        type = "password2"
        value = {password2}
        onChange={(e) => setPassword2(e.target.value)}
        required
        />
      <br />

      <button
        type="submit"
        className="pretty-log"
      >
        Register
      </button>
    </form>
  );
}

export default LoginForm;
