import React, { useState } from 'react';
import './login.css';

function LoginForm({ onLogin = () => {} }) {
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState(''); // optional if you need a username
  const [password, setPassword] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    // Basic client-side validation (optional but recommended):
    if (!email || !password) {
      alert('Please fill out all required fields.');
      return;
    }

    // Call the onLogin callback with the form data
    onLogin({ email, username, password });
    console.log('Form Data:', { email, username, password });
  };

  return (
    <form onSubmit={handleSubmit} className="login-form">
      <label className="Header2" htmlFor="email-input">
        Email:
      </label>
      <input
        className="form-input"
        id="email-input"
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
      />

      <br />

      {/* Remove or rename this field if your app doesn't need a username */}
      <label className="Header2" htmlFor="username-input">
        Username (optional):
      </label>
      <input
        className="form-input"
        id="username-input"
        type="text"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />

      <br />

      <label className="Header2" htmlFor="password-input">
        Password:
      </label>
      <input
        className="form-input"
        id="password-input"
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
      />

      <br />

      <button
        type="submit"
        className="pretty-log"
      >
        Login
      </button>
    </form>
  );
}

export default LoginForm;
