import React, { useState, useRef, useEffect} from 'react';
import {faCheck, faTimes, faInfoCircle} from "@fortawesome/free-solid-svg-icons"
import {FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import './register.css';



// 3-23 letters 
const USER_REGEX = /^[a-zA-Z][a-zA-Z0-9-_]{3,23}$/;

// TODO 

// 1 lower case letter, 1 upper case letter, 1 digit, 1 special character 
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;


const RegisterForm = () => {

    const FORBIDDEN_WORDS = [
    "fuck",
];

    const userRef = useRef();
    const errRef = useRef();

    // email
    const[email, setEmail] = useState('');
    const[validEmail, setValidEmail] = useState(false);
    const [emailFocus, setEmailFocus] = useState(false);

    // userName 
    const [user, setUser] = useState('');
    const [validName, setValidName] = useState(false);
    const [userFocus, setUserFocus] = useState(false);

    //password
    const [pwd, setPwd] = useState('');
    const [validPwd, setValidPwd] = useState(false);
    const [pwdFocus, setPwdFocus] = useState(false);

    const [matchPwd, setMatchPwd] = useState('');
    const [validMatch, setValidMatch] = useState(false);
    const [matchFocus, setMatchFocus] = useState(false);

    const[errMsg, setErrMsg] = useState('');
    const[success, setSucess] = useState(false);

     



   
    useEffect(() => {
        if (user === '') {
        setValidName(false); // Empty username is not valid
    }  else {
       const result = USER_REGEX.test(user);
        console.log(result);
        console.log(user);
        setValidName(result);
    }
    }, [user])

    


     useEffect(() => {
        const result = PWD_REGEX.test(pwd);
        console.log(result);
        console.log(pwd);
        setValidPwd(result);
        const match = pwd === matchPwd;
        setValidMatch(match);
        setValidName(result);
    }, [pwd, matchPwd])

    useEffect(()=> {
        setErrMsg('');
        console.log(errMsg);
        // error message will clear whenever any of these change 
    }, [user, pwd, matchPwd])

    

   

    return  (
        
   <section>
    <form> 
    <p ref = {errRef} className = {errMsg ? "errmsg" :
        "offscreen"} aria-live = "assertive">{errMsg}</p>
    <label className="Header2" htmlFor="email">  
        Email:
         <span className = {validEmail ? "valid" : "hide"}>
            <FontAwesomeIcon icon = {faCheck} />
       </span>

       <span className = {validEmail || !email ? "hide" : "invalid"}>
            <FontAwesomeIcon icon = {faTimes}/>
       </span>
    </label>
        <input
        className="form-input"
        id = "email"
        type = "email"
        ref = {userRef}
        autoComplete = "off"
        onChange = {(e) => setEmail(e.target.value)}
        required
        aria-invalid = {validEmail ? "false": true}
        aria-describedby="uidnote"
        onFocus = {() => setEmailFocus(true)}
        onBlur = {() => setEmailFocus(false)}
      /> 
       <p id = "uidnote" className = {emailFocus && email && !validEmail ? "instructions" : "offscreen"}>
            <FontAwesomeIcon icon = {faInfoCircle} />
            Must be a valid email.<br></br> 
        </p>
      
 
    

    <label className = "Header2" htmlFor = "username"> 
       UserName:

         <span className={validName && userFocus && user ? "valid" : "hide"}>
            <FontAwesomeIcon icon={faCheck} />
        </span>
        <span className={!validName && userFocus && user ? "invalid" : "hide"}>
            <FontAwesomeIcon icon={faTimes} />
        </span>
       
       </label>
        <input
        className="form-input"
        id = "username"
        type = "text"
        ref = {userRef}
        autoComplete = "off"
        onChange = {(e) => setUser(e.target.value)}
        aria-invalid = {validName ? "false": true}
        aria-describedby="uidnote"
        onFocus = {() => setUserFocus(true)}
        onBlur = {() => setUserFocus(false)}
        /> 
        <p id = "uidnote" className = {userFocus && user && !validName ? "instructions" : "offscreen"}>
            <FontAwesomeIcon icon = {faInfoCircle} />
            4 to 20 characters. <br />
            Must begin with a letter. <br/>
            Letters, numbers, hyphens, underscores allowed. 
        </p>



        <label className= "Header2" htmlFor = "password">
            password:
        </label>
        
       

    </form>
   </section> 
  )
}

export default RegisterForm;
