import React, { useState, useRef, useEffect} from 'react';
import {faCheck, faTimes, faInfoCircle} from "@fortawesome/free-solid-svg-icons"
import {FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import './register.css';



// 3-23 letters 
const USER_REGEX = /^[a-zA-Z][a-zA-Z0-9-_]{3,23}$/;

const EMAIL_REGEX = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;


// 1 lower case letter, 1 upper case letter, 1 digit, 1 special character 
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;


const RegisterForm = () => {

    const userRef = useRef();
    const errRef = useRef();

    // email
    const[email, setEmail] = useState('');
    const[validEmail, setValidEmail] = useState(false);
    const [emailFocus, setEmailFocus] = useState(false);

    // userName 
    const [user, setUser] = useState('');
    const [validName, setValidName] = useState(false);
    const [userFocus, setUserFocus] = useState(false);

    //password
    const [pwd, setPwd] = useState('');
    const [validPwd, setValidPwd] = useState(false);
    const [pwdFocus, setPwdFocus] = useState(false);

    //2nd password 
    const [matchPwd, setMatchPwd] = useState('');
    const [validMatch, setValidMatch] = useState(false);
    const [matchFocus, setMatchFocus] = useState(false);

    const[errMsg, setErrMsg] = useState('');
    const[success, setSucess] = useState(false);

     



    useEffect(() => {
        userRef.current.focus();
    }, [])

     useEffect(() => {
        setValidName(USER_REGEX.test(user));
    }, [user])

    useEffect(() => {
        setValidEmail(EMAIL_REGEX.test(email));
    }, [email])


    // matcher 
     useEffect(() => {
        if(matchPwd == '') {
            setValidMatch(false);
        }
        const result = PWD_REGEX.test(pwd);
        console.log(result);
        console.log(pwd);
        setValidPwd(result);
        const match = pwd === matchPwd;
        setValidMatch(match);
    }, [pwd, matchPwd])

    useEffect(()=> {
        setErrMsg('');
        console.log(errMsg);
        // error message will clear whenever any of these change 
    }, [user, pwd, matchPwd])

    

   

return  (
        
   <section>
    <form> 
    <p ref = {errRef} className = {errMsg ? "errmsg" :
        "offscreen"} aria-live = "assertive">{errMsg}</p>
     
    <label className = "Header2" htmlFor = "username"> 
       UserName:

        <FontAwesomeIcon icon={faCheck} className={validName ? "valid" : "hide"} />
        <FontAwesomeIcon icon={faTimes} className={validName || !user ? "hide" : "invalid"} />
       
       </label>
        <input
        className="form-input"
        id = "username"
        type = "text"
        ref = {userRef}
        autoComplete = "off"
        onChange = {(e) => setUser(e.target.value)}
        aria-invalid = {validName ? "false": "true"}
        aria-describedby="uidnote"
        onFocus = {() => setUserFocus(true)}
        onBlur = {() => setUserFocus(false)}
        /> 
        <p id = "uidnote" className = {userFocus && user && !validName ? "instructions" : "offscreen"}>
            <FontAwesomeIcon icon = {faInfoCircle} />
            4 to 24 characters. <br />
            Must begin with a letter. <br/>
            Letters, numbers, hyphens, underscores allowed. 
        </p>





        <label className="Header2" htmlFor="email">  
            Email:
            <FontAwesomeIcon icon = {faCheck} className = {validEmail ? "valid" : "hide"}/>
            <FontAwesomeIcon icon = {faTimes} className = {validEmail || !email ? "hide" : "invalid"}/>
      

        </label>
            <input
            className="form-input"
            id = "email"
            type = "email"
            ref = {userRef}
            autoComplete = "off"
            onChange = {(e) => setEmail(e.target.value)}
            required
            aria-invalid = {validEmail ? "false": "true"}
            aria-describedby="uidnote"
            onFocus = {() => setEmailFocus(true)}
            onBlur = {() => setEmailFocus(false)}
        />
        <p id = "uidnote" className = {emailFocus && email && !validEmail ? "instructions" : "offscreen"}>
                <FontAwesomeIcon icon = {faInfoCircle} />
                Must be a valid email.<br></br>
        </p>

        <label className = "Header2" htmlFor="password">
            Password: 
            <FontAwesomeIcon icon = {faCheck} className = {validPwd ? "valid" : "hide"} />
            <FontAwesomeIcon icon = {faTimes} className = {validPwd || !pwd ? "hide" : "invalid"}/>
       
        </label>
        <input
            className="form-input"
            id = "password"
            type = "password"
            ref = {userRef}
            autoComplete = "off"
            onChange = {(e) => setPwd(e.target.value)}
            required
            aria-invalid = {validPwd ? "false": "true"}
            aria-describedby="uidnote"
            onFocus = {() => setPwdFocus(true)}
            onBlur = {() => setPwdFocus(false)}
        />

         <p id = "uidnote" className = {pwdFocus && pwd && !validPwd ? "instructions" : "offscreen"}>
                <FontAwesomeIcon icon = {faInfoCircle} />
                Must be a valid Password .<br/>
                Must contain: 1 lower case letter, 1 upper case letter, 1 digit, 1 special character .<br/>

        </p>

        <label className = "Header2" htmlFor="password">
            Confirm Password: 
            <FontAwesomeIcon icon = {faCheck} className = {validMatch ? "valid" : "hide"} />
            <FontAwesomeIcon icon = {faTimes} className= {validMatch || !pwd ? "hide" : "invalid"}/>
       
        </label>
        <input
            className="form-input"
            id = "password"
            type = "password"
            ref = {userRef}
            autoComplete = "off"
            onChange = {(e) => setPwd(e.target.value)}
            required
            aria-invalid = {validPwd ? "false": "true"}
            aria-describedby="uidnote"
            onFocus = {() => setPwdFocus(true)}
            onBlur = {() => setPwdFocus(false)}
        />

         <p id = "uidnote" className = {pwdFocus && !setValidMatch ? "instructions" : "offscreen"}>
                <FontAwesomeIcon icon = {faInfoCircle} />
                Passwords Must Match 

        </p>

        
        
    </form>
   </section> 
  )
}

export default RegisterForm;
