import React, { useState } from 'react';
import './addPre.css'; // Ensure this file contains the updated CSS styles

function AddPreWorkout() {
    const [formData, setFormData] = useState({
        Rating: '',
        Name: '',
        Flavor: '',
        Price: '',
        Brand: '',
        Caffeine: '',
        L_Citrulline: '',
        Beta_Alanine: '',
        BCAAS: '',
        Creatine_Monohydrate: '',
        Beetroot_Extract: '',
        Pomegranate_Extract: '',
        L_Glutamine: '',
        Vasodilators: '',
        Vitamin_b: '',
    });

    // Handle change, update form when changes occur on the web page 
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevState) => ({
            ...prevState,
            // restricts rating to an integer 
            [name]: name === 'rating' ? Math.max(0, Math.min(10, Number(value))) : value,
        }));
    };

 

    const handleSubmit = (event) => {
        event.preventDefault();
        alert('Pre-workout form submitted! (Server interaction removed)');
        console.log('Form Data:', formData);
    };

    return (
        <div className="New-Background">

            <form className="formatted-form" onSubmit={handleSubmit}>
            
                <button className="nice-button" type="submit">
                    Add Pre-Workout
                </button>
            </form>

            <h2 className="header header--small">Necessary Components</h2>
            <form className="formatted-form" onSubmit={handleSubmit}>
                 <input
                    className="form-input"
                    type="number"
                    value={formData.rating}
                    onChange={handleChange}
                    name="rating"
                    placeholder="Rating"
                />
                <input
                    className="form-input"
                    type="text"
                    value={formData.name}
                    onChange={handleChange}
                    name="name"
                    placeholder="Name"
                />
                <input
                    className="form-input"
                    type="text"
                    value={formData.flavor}
                    onChange={handleChange}
                    name="flavor"
                    placeholder="Flavor"
                />
                <input
                    className="form-input"
                    type="number"
                    value={formData.price}
                    onChange={handleChange}
                    name="price"
                    placeholder="Price"
                />
                    <input 
                    className= "form-input"
                    type = "text"
                    value = {formData.Brand}
                    onChange={handleChange}
                    name = "brand"
                    placeholder="Brand"
                    />

                 <h3 className="header header--small">Ingredients</h3>

            </form>

            

    
        </div>
    );
}

export default AddPreWorkout;
