import React, { useState, useRef, useEffect} from 'react';
import './login.css';



// 3-23 letters 
const USER_REGEX = /^[a-zA-Z][a-zA-Z0-9-_]{3,23}$/;
// 1 lower case letter, 1 upper case letter, 1 digit, 1 special character 
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;


const LoginForm = () => {

    const userRef = useRef();
    const errRef = useRef();

    // email
    const[email, setEmail] = useState('');
    const[validEmail, setValidEmail] = useState(false);
 
    // userName 
    const [user, setUser] = useState('');
    // does the name validate 
    const [validName, setValidName] = useState(false);
    const [userFocus, setUserFocus] = useState(false);


    const [pwd, setPwd] = useState('');
    const [validPwd, setValidPwd] = useState(false);
    const [pwdFocus, setPwdFocus] = useState(false);

    const [matchPwd, setMatchPwd] = useState('');
    const [validMatch, setValidMatch] = useState(false);
    const [matchFocus, setMatchFocus] = useState(false);



    useEffect(() => {
        userRef.current.focus();
    }, [])

    useEffect(() => {
        const result = USER_REGEX.test(user);
        console.log(result);
        console.log(user);
        setValidName(result);
    }, [user])


     useEffect(() => {
        const result = PWD_REGEX.test(pwd);
        console.log(result);
        console.log(pwd);
        setValidPwd(result);
        const match = pwd == matchPwd;
        setValidMatch(match);
        setValidName(result);
    }, [user])



    
   

    return  (
    <form onSubmit={handleSubmit} className="login-form">
      <label className="Header2" htmlFor="email-input">
        Email:
      </label>
      <input
        className="form-input"
        id="email-input"
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
      />


      <br />


      {}
      <label className="Header2" htmlFor="username-input">
        Username (optional):
      </label>
      <input
        className="form-input"
        id="username-input"
        type="text"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />


      <br />


      <label className="Header2" htmlFor="password-input">
        Password:
      </label>
      <input
        className="form-input"
        id="password-input"
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
      />
    <label className="Header2" htmlFor="password-input">
         Re-Enter Password:
    </label>
      <input
        className="form-input"
        id = "password2-input"
        type = "password2"
        value = {password2}
        onChange={(e) => setPassword2(e.target.value)}
        required
        />
      <br />


      <button
        type="submit"
        className="pretty-log"
      >
        Register
      </button>
    </form>
  );
}

export default LoginForm;
